import React, { useState , useEffect } from 'react';
import axios from 'axios';
import './App.css';
import Slider from './components/Slider/Slider'

function App() {
  const [radius, setRadius] = useState(1);
  const [introduction, setIntroduction] = useState("");
  const [latitude, setLatitude] = useState(0.0);
  const [longitude, setLongitude] = useState(0.0);

  var peerConnection;
  var localStream;
  var remoteStream;

  useEffect(() => {
    if ("geolocation" in navigator) {
      navigator.geolocation.getCurrentPosition(function(position) {
        setLatitude(position.coords.latitude);
        console.log("Latitude: " + latitude);
        setLongitude(position.coords.longitude);
        console.log("Longitude: " + longitude);
      }, function(error) {
        console.error("Error Code = " + error.code + " - " + error.message);
      });
    }
    else {
      console.log("Geolocation API is not available in your browser.");
    }
  });

  async function initialize() {
    peerConnection = new RTCPeerConnection();
    localStream = await navigator.mediaDevices.getUserMedia({video:true, audio:false});
    remoteStream = new MediaStream();

    // document.getElementById('user-1').srcObject = localStream;
    // document.getElementById('user-2').srcObject = remoteStream;

    localStream.getTracks().forEach((track) => {
        peerConnection.addTrack(track, localStream);
    });

    peerConnection.ontrack = (event) => {
        event.streams[0].getTracks().forEach((track) => {
        remoteStream.addTrack(track);
        });
    };
  }

  async function Create_Offer() {
    const offer = await peerConnection.createOffer();
    await peerConnection.setLocalDescription(offer);
  }

  useEffect(() => {
    initialize();
    console.log(peerConnection, localStream, remoteStream)
  }, []);

  async function Add_To_Waitlist() {
    await Create_Offer();
    let SDP = [JSON.stringify(peerConnection.localDescription), null];
    console.log(SDP);
    axios.post('http://localhost:8080/add_to_waitlist',
        {
          'client' :
          {
          'info':introduction,
          'radius':radius,
          'lat':latitude,
          'lon':longitude,
          },
          'SDP' : SDP
        }).then(res => {
          console.log(res.data);
    });
  }

  async function Radius_Match() {
    axios.post('http://localhost:8080/radius_match', 
      {
        'info':introduction,
        'radius':radius,
        'lat':latitude,
        'lon':longitude
      }).then(res => {
        res = res.data;
        let message = res['message'];
        if (message == 'Continue')
          Radius_Match();
        else if (message == 'EOL')
          Add_To_Waitlist();
        else {
          console.log('match');
        }
    });
  }

  async function Add_Answer() {
    /*Add 6's SDP offer to 2's remote stream*/
    console.log('Add answer triggered')

    // need to poll for answer first
    // let answer = JSON.parse(document.getElementById('answer-sdp').value)
    // console.log('answer:', answer)
    // if (!peerConnection.currentRemoteDescription){
    //     peerConnection.setRemoteDescription(answer);
    // }

    /*Remove client 2 from waiting list */
    axios.post('http://localhost:8080/remove_waitlist', 
      {
        'client' :
        {
        'info':introduction,
        'radius':radius,
        'lat':latitude,
        'lon':longitude,
        },
        'SDP' : SDP
      }).then(res => {
        console.log(res.data);
        });
    /*Remove client 2 from to_be_matched */

    /*add client1:client2 -> signifies to client 1 that client 2 has agreed*/
    /*then remove client2:client1*/

  }

  function handleIntroInput(e) {
    setIntroduction(e.target.value)
  };

  async function QueryUser(e) {
    e.preventDefault();
    Radius_Match();
  }

  return (
    <div className="App">
      <h2>Friend.me</h2>

      {/*Maybe make a componenet for the videos*/}
      <div id = "videos">
        <video className="video-player" id="user-1" autoPlay playsInline></video>
        <video className="video-player" id="user-2" autoPlay playsInline></video>
      </div>

      <label htmlFor="userInput">Tell us a little bit about yourself: </label>
      <input type="text" id="userInput" name="userInput" placeholder="Type your introduction" value = {introduction} onChange = {handleIntroInput}/>

      <Slider radius = {radius} setRadius = {setRadius}/>

      <SubmitButton title="find" onClick={QueryUser}/>
    </div>
  );
}

function SubmitButton({title, onClick}) {
  return (
    <button onClick={onClick}> {title} </button>
  )
}

export default App;
